import 'dart:developer';
import 'package:flutter/material.dart';
import 'package:mobile_najdiprevoz/interfaces/trip-list-response.dart';
import 'package:mobile_najdiprevoz/interfaces/user-short-response.dart';
import 'package:mobile_najdiprevoz/services/auth.service.dart';
import 'package:mobile_najdiprevoz/shared-widgets/trip-card.dart';

UserShortResponse user = new UserShortResponse(1, "Vasko Jovanoski", 3.5,
    "");

class HomeScreen extends StatelessWidget {
  HomeScreen({Key key, this.token}) : super(key: key);

  final String token;
  final tripListResponse = new TripListResponse(
      1,
      "Struga",
      "Ohrid",
      3,
      "ACTIVE",
      5,
      DateTime.now().add(new Duration(days: 0, hours: 12, minutes: 0)),
      200,
      true,
      [],
      user);

  factory HomeScreen.fromBase64(String token) => HomeScreen(token: token);

  @override
  Widget build(BuildContext context) => Scaffold(
      appBar: AppBar(title: Text("Trips")),
      backgroundColor: Colors.grey.shade200,
      body: ListView(
        children: [
          TripCard(trip: tripListResponse),
          TripCard(trip: tripListResponse),
          TripCard(trip: tripListResponse),
          TripCard(trip: tripListResponse),
          TripCard(trip: tripListResponse),
          TripCard(trip: tripListResponse),
          TripCard(trip: tripListResponse)
        ],
      ));
}

class LogoutButton extends StatelessWidget {
  @override
  Widget build(BuildContext context) {
    return FlatButton(
        child: Text("Logout"),
        onPressed: () async => AuthService().logout(context));
  }
}

